import React, { Component } from "react";
import "./App.css";
//import Person from "./Person/Person.js";

class App extends Component {
  state = {
    userInput: "",
    userOutput: ""
  };

  inputChangeHandler = event => {
    this.setState({
      userInput: event.target.value,
      userOutput: event.target.value
    });
  };

  render() {
    return (
      <div className="App">
        <input type="text" onChange={this.inputChangeHandler} /> <br />
        {this.state.userOutput}
      </div>
    );
  }
}

export default App;
